import img1 from './img/applephone.jpg'

export default class ListPhone extends Component {
    arr = ['applephone.jpg', 'applephone.jpg']
    render() {
        

        console.log();
        return (
            <div>
                {/* sử dụng import để gắn hình ảnh vào thẻ img */}
                {/* <img src={img1} alt=''/> */}

                {/* Cách 2 là để hình ảnh nằm trong public và tưởng tượng đường dẫn bắt đầu từ file index.html */}
                {/* <img src='./img/applephone.jpg' alt=''/> */}


                <h1>Danh sách sản phẩm</h1>
                <div className="row">
                    {this.arrPhone.map((item, index) => {
                        return (
                            <div className="col-4" key={index}>
                                <img src={item.hinhAnh} alt="" />
                                <h2>{item.tenSP}</h2>
                                <button className="btn btn-success">Xem chi tiết</button>
                            </div>
                        );
                    })}
                </div>

                <div className='row'>
                    <div className='col-4'>

                    </div>
                </div>
            </div>
        )
    }
}
