import React, { Component } from 'react'
import img1 from './img/applephone.jpg'

export default class ListPhone extends Component {
    arr = ['applephone.jpg', 'applephone.jpg']
    render() {
        arrPhone = [
            {
                maSP: 1,
                tenSP: 'VinSmart Live',
                manHinh: 'AMOLED, 6.2, Full HD+',
                heDieuHanh: 'Android 9.0 (Pie)',
                cameraTruoc: '20 MP',
                cameraSau: 'Chính 48 MP & Phụ 8 MP, 5 MP',
                ram: '4 GB',
                rom: '64 GB',
                giaBan: 5700000,
                hinhAnh: './image/vsphone.jpg',
            },
            {
                maSP: 2,
                tenSP: 'Meizu 16Xs',
                manHinh: 'AMOLED, FHD+ 2232 x 1080 pixels',
                heDieuHanh: 'Android 9.0 (Pie); Flyme',
                cameraTruoc: '20 MP',
                cameraSau: 'Chính 48 MP & Phụ 8 MP, 5 MP',
                ram: '4 GB',
                rom: '64 GB',
                giaBan: 7600000,
                hinhAnh: './image/sp_note7.png',
            },
            {
                maSP: 3,
                tenSP: 'Iphone XS Max',
                manHinh: 'OLED, 6.5, 1242 x 2688 Pixels',
                heDieuHanh: 'iOS 12',
                cameraSau: 'Chính 12 MP & Phụ 12 MP',
                cameraTruoc: '7 MP',
                ram: '4 GB',
                rom: '64 GB',
                giaBan: 27000000,
                hinhAnh: './image/applephone.jpg',
            },
        ];

        console.log();
        return (
            <div>
                {/* sử dụng import để gắn hình ảnh vào thẻ img */}
                {/* <img src={img1} alt=''/> */}

                {/* Cách 2 là để hình ảnh nằm trong public và tưởng tượng đường dẫn bắt đầu từ file index.html */}
                {/* <img src='./img/applephone.jpg' alt=''/> */}


                <h1>Danh sách sản phẩm</h1>
                <div className="row">
                    {this.arrPhone.map((item, index) => {
                        return (
                            <div className="col-4" key={index}>
                                <img src={item.hinhAnh} alt="" />
                                <h2>{item.tenSP}</h2>
                                <button className="btn btn-success">Xem chi tiết</button>
                            </div>
                        );
                    })}
                </div>

                <div className=''></div>
            </div>
        )
    }
}
