import React, { Component } from 'react'

export default class FormProduct extends Component {
    state ={
        id: '',
        image: '',
        name: '',
        productType: '',
        price: '',
        descrip: '',
    };
    getValueInput = (event) => {
        let {value, id} = event.target;
        this.setState({
            [id]: value,
        });
    };
    render() {
        return (
            <div>
                <form class="card text-white bg-success bg-opacity-50">
                    <div class="card-body">
                        <div className="row mb-3">
                            <div className="col-6">
                                <label htmlFor="">Id</label>
                                <input
                                // onChange={(event) => {
                                //     // lấy dữ liệu từ người dùng
                                //     // event.target giúp trỏ tới thẻ đang có sự kiện
                                //     // let data = event.target.value;
                                //     // console.log(data);
                                //     // this.setState({
                                //     //     id: data
                                //     // });
                                //     // let {value, id} = event.target;
                                //     // this.setState({
                                //     //     [id]: value,
                                //     // });
                                // }}
                                onChange={this.getValueInput}
                                className="form-control" 
                                type="text" 
                                id="id" />
                            </div>
                            <div className="col-6">
                                <label htmlFor="">Image</label>
                                <input onChange={this.getValueInput}
                                className="form-control" 
                                type="text" 
                                id="image" />
                            </div>
                        </div>
                        <div className="row mb-3">
                            <div className="col-6">
                                <label htmlFor="">Name</label>
                                <input 
                                onChange={this.getValueInput}
                                className="form-control" 
                                type="text" 
                                id="name" />
                            </div>
                            <div className="col-6">
                                <label htmlFor="">Product Type</label>
                                <input 
                                onChange={this.getValueInput}
                                className="form-control" 
                                type="text" 
                                id="productType" />
                            </div>
                        </div>
                        <div className="row mb-3">
                            <div className="col-6">
                                <label htmlFor="">Price</label>
                                <input 
                                onChange={this.getValueInput}
                                className="form-control" 
                                type="text" 
                                id="price" />
                            </div>
                            <div className="col-6">
                                <label htmlFor="">Description</label>
                                <input 
                                className="form-control" 
                                type="text" id="descrip" />
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}
